import React, { Component } from 'react';
import './Recipe.css';


class Recipe extends Component {
    render(){
        const {title} = this.props;
        const ingredients = this.props.ingredients.map((ing, index) => (
            <li key={index}>{ing}</li>
        ))
        return(
            <div className="App">
              <div> Recipe {title} </div>
               <ul>
                {ingredients}
               </ul>
            </div>
        );
    }
}


export default Recipe;
